puts "Bienvenue dans cet outil très intelligent de big data."
puts "Veuillez choisir parmi les options suivantes"
puts "1: Le nombre de handles dans la liste"
puts "2: Le plus petit handle dans la liste"
puts "3: Le nombre de handles comportant 5 caractères"
puts "4: Le nombre de handles commençant par une majuscule"
puts "5: La liste des handles dans l'ordre alphabétique"
puts "6: La liste des handles par ordre de taille"
puts "7: La position dans la liste de la personne @epenser"
puts "8: La répatition des élements de la liste par taille"
print "> "

n = gets.chomp.to_i

case n
when 1


    #1
    puts "Il y a #{journalists.length} handles dans la liste"

when 2

    #2
    puts "Le plus petit handle est : #{journalists.sort_by(&:length)[0]}"

when 3
    #3
    five_characters_handles = []

    for element in journalists do 
        if element.length == 6
            five_characters_handles << element
        end 
    end

    puts "Il y a #{five_characters_handles.length} handles de 5 caractères"

when 4

    #4

    handles_starting_with_caps = []

    for element in journalists do
        if element =~ /^@[A-Z]/
            handles_starting_with_caps << element
        end
    end

    puts "Il y a #{handles_starting_with_caps.length} handles commençant par une majuscule"

when 5

    #5


    handles_alphabetical_order = journalists.sort

    puts "Voici la liste des handles par ordre alphabétique"
    puts handles_alphabetical_order

when 6

    #6
    handles_size_order = journalists.sort_by(&:length)

    puts "Voici la liste des handles par ordre de taille"
    puts handles_size_order

when 7

    #7

    puts "@epenser se trouve à l'index : #{journalists.find_index("@epenser")}"

when 8

    #8

    tailles = Hash.new(0)

    journalists.each do |element|
        taille = element.length
        tailles[taille] += 1
    end

    puts "Voici la répartition des handles par taille : "
    puts tailles

end